import React, { useContext, useState } from 'react';
import { AppContext } from '../../context/AppContext';

const TotalBudget = () => {
  const { budget, dispatch } = useContext(AppContext);

  const [theBudget, setBudget] = useState(budget);
  const [isEditing, setIsEditing] = useState(false);

  // const [buttonSave, setButtonSave] = useState('display-none');
  // const [buttonEdit, setButtonEdit] = useState('btn btn-primary');

  const onEdit = () => {
    setIsEditing(true);
    // setButtonEdit('display-none');
    // setButtonSave('btn btn-primary');
  };
  const onSave = (event) => {
    setIsEditing(false);
    // setButtonEdit('btn btn-primary');
    // setButtonSave('display-none');

    event.preventDefault();
    dispatch({
      type: 'UPDATE_BUDGET',
      payload: theBudget,
    });
  };

  // const buttonStyle = {
  //   position: "absolute",
  // }

  return (
    <div className="col-sm">
      <div
        className="alert alert-secondary"
        style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}
      >
        <span>Budget: &</span>
        {isEditing ? (
          <input
            type="text"
            value={theBudget}
            onChange={(event) => setBudget(event.target.value)}
            style={{
              display: 'inline-block',
              marginRight: 'auto',
              width: '48px',
            }}
          ></input>
        ) : (
          <span
            style={{
              display: 'inline-block',
              marginRight: 'auto',
              width: '48px',
            }}
          >
            {theBudget}
          </span>
        )}
        {isEditing ? (
          <button className="btn btn-primary" onClick={onSave} button>
            Save
          </button>
        ) : (
          <button className="btn btn-primary" onClick={onEdit}>
            Edit
          </button>
        )}
      </div>
    </div>
  );
};

export default TotalBudget;
